// order.component.ts

import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from "@angular/router";
import { CouponService } from 'path-to-your-coupon-service';

@Component({
  selector: 'app-order',
  templateUrl: './order.component.html',
  styleUrls: ['./order.component.css']
})
export class OrderComponent implements OnInit {
  couponName!: string;
  couponDescription!: string;
  couponPrice!: number;
  couponValidTo!: string;

  constructor(
    private route: ActivatedRoute,
    private couponService: CouponService
  ) {
    this.route.params.subscribe(params => {
      this.couponName = params['name'];
      this.couponDescription = params['description'];
      this.couponPrice = params['price'];
      this.couponValidTo = params['lastUpdateData'];
    });
  }

  ngOnInit(): void {
    const userLogin = localStorage.getItem('userLogin');
    console.log('User Login:', userLogin);
  }

  addToCart(): void {
    const userLogin = localStorage.getItem('userLogin');
    if (userLogin) {
      this.couponService.buyCoupon(userLogin, this.couponName).subscribe(
        response => {
          console.log('Purchase successful:', response);
          // Handle success, e.g., show a success message
        },
        error => {
          console.error('Error during purchase:', error);
          // Handle error, e.g., show an error message
        }
      );
    }
  }
}
