import {Component, Input, OnInit} from '@angular/core';

@Component({
  selector: 'app-coupon',
  templateUrl: './coupon.component.html',
  styleUrls: ['./coupon.component.css']
})
export class CouponComponent implements OnInit {
  constructor( private filteredCoupons) {
  }
  @Input() coupons: any;

  searchTerm: string = '';

  filteredCoupons: any[];

  scrollToTop(): void {
    window.scrollTo(0, 0);
  }

  windowScrolled = false;

  ngOnInit() {
    window.addEventListener('scroll', () => {
      this.windowScrolled = window.pageYOffset !== 0;
    });

    this.filteredCoupons = this.coupons;
  }

  updateFilteredCoupons() {
    if (!this.searchTerm) {
      // If search term is empty, show all coupons
      this.filteredCoupons = this.coupons;
    } else {
      // If search term is present, filter coupons by name
      this.filteredCoupons = this.coupons.filter((coupon: any) =>
        coupon.name.toLowerCase().includes(this.searchTerm.toLowerCase())
      );
    }
  }
}
