// placing.component.ts

import { Component, OnInit } from '@angular/core';
import { UserCouponService } from 'path-to-your-user-coupon-service';

@Component({
  selector: 'app-placing',
  templateUrl: './placing.component.html',
  styleUrls: ['./placing.component.css']
})
export class PlacingComponent implements OnInit {
  userCoupons: any[] = [];

  constructor(private userCouponService: UserCouponService) {}

  ngOnInit(): void {
    const userLogin = localStorage.getItem('userLogin');
    console.log('User Login:', userLogin);

    if (userLogin) {
      this.userCouponService.getUserCoupons(userLogin).subscribe(
        (coupons: any[]) => {
          this.userCoupons = coupons;
        },
        error => {
          console.error('Error fetching user coupons:', error);
        }
      );
    }
  }
}
