import {Component, Injectable, OnInit, ViewChild} from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {BehaviorSubject} from "rxjs";

@Injectable({
  providedIn: 'root'
})
export class DataTransferService {
  private dataVariable = new BehaviorSubject<any>(null);

  setData(data: any) {
    this.dataVariable.next(data);
  }

  getData() {
    return this.dataVariable.asObservable();
  }
}

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent /*implements OnInit*/ {
  constructor(private http: HttpClient, private dataTransferService: DataTransferService) {}
  getData() {
    this.http.get('http://localhost:8086/certificate/all/2').subscribe((data: any) => {
      this.dataTransferService.setData(data); // share the data using the DataTransferService
    });
  }

}
