import { Component, Input, OnInit, OnChanges, SimpleChanges } from '@angular/core';

interface Coupon {
  name: string;
  description: string;
  price: number;
  lastUpdateData: string;
}
// ... (other imports)

@Component({
  selector: 'app-coupon',
  templateUrl: './coupon.component.html',
  styleUrls: ['./coupon.component.css']
})
export class CouponComponent implements OnInit, OnChanges {
  @Input() coupons: Coupon[] = [];
  searchTerm: string = '';
  filteredCoupons: Coupon[] = [];

  // Add a property to track whether infinite scroll is active
  infiniteScrollActive = false;

  scrollToTop(): void {
    window.scrollTo(0, 0);
  }

  windowScrolled = false;

  ngOnInit() {
    window.addEventListener('scroll', () => {
      this.windowScrolled = window.pageYOffset !== 0;
      this.checkInfiniteScroll();
    });

    // Initial loading of all coupons
    this.filteredCoupons = this.coupons;
  }

  ngOnChanges(changes: SimpleChanges) {
    if ((changes as any).coupons) {
      // If infinite scroll is active, append new data
      if (this.infiniteScrollActive) {
        this.filteredCoupons.push(...this.coupons);
        this.infiniteScrollActive = false;
      } else {
        this.filteredCoupons = this.coupons;
      }
    }
  }

  search() {
    const searchTerm = this.searchTerm.toLowerCase();

    this.filteredCoupons = this.coupons.filter(coupon => {
      const name = coupon.name.toLowerCase();
      return name.includes(searchTerm);
    });
  }

  // Function to check if infinite scroll is active
  checkInfiniteScroll() {
    const scrollHeight = document.documentElement.scrollHeight;
    const scrollTop = window.pageYOffset || document.documentElement.scrollTop;
    const clientHeight = document.documentElement.clientHeight;

    // Adjust the threshold as needed
    const scrollThreshold = 100;

    if (scrollHeight - (scrollTop + clientHeight) < scrollThreshold) {
      this.infiniteScrollActive = true;
      // Trigger a function to load more data (e.g., through a service)
      // this.loadMoreData();
    }
  }
}
<header>
    <nav>
        <ul>
            <li><span class="material-icons">reorder</span></li>
            <li><input type="text" [(ngModel)]="searchTerm" placeholder="Search" (input)="search()" ></li>
            <li>
                <select id="category">
                    <option value="category1">Category 1</option>
                    <option value="category2">Category 2</option>
                    <option value="category3">Category 3</option>
                </select>
            </li>
            <li><span class="material-icons">favorite</span></li>
            <li><span class="material-icons">shopping_cart</span></li>
        </ul>
    </nav>
    <div class="user-actions">
        <a href="/login">Login</a> |
      <a href="/registration">Sign Up</a> |
      <a href="/creation">Creat Coupon</a>
    </div>
</header>
<main>
    <div class="page-title">
        <h1>Coupons</h1>
    </div>
    <div class="categories">
        <div class="category"> <img src="../../../../assets/images/category.jpg" alt="Category 1"></div>
        <div class="category"><img src="../../../../assets/images/category.jpg" alt="Category 2"></div>
        <div class="category"><img src="../../../../assets/images/category.jpg" alt="Category 3"></div>
    </div>

    <div id="coupons" class="coupons-table"  >
      <ng-container *ngFor="let coupon of filteredCoupons">
            <div class="coupon"><img src="../../../../assets/images/coupon1.jpg" alt="Short description">
              <p>Name: {{coupon.name}}</p>
              <p>Short description: {{coupon.description}} </p>
              <p>Price: {{coupon.price| price:'UAH':'left' }}</p>
              <p>Valid until: {{coupon.lastUpdateData}}</p>
            </div>
      </ng-container>
        </div>
  <div class="to-top" (click)="scrollToTop()" [ngClass]="{ 'show-scrollTop': windowScrolled }">
    <img src="../../../../assets/images/topUp.jpg" alt="top"/>
  </div>
</main>
